INSERT INTO `wp_postmeta` VALUES("2541", "612", "product-text", "<p><strong>Фармакологическое действие </strong></p>\r\n<ul>\r\n	<li>Гипотензивный препарат центрального действия, стимулирует постсинаптические альфа2-адренорецепторы сосудодвигательного центра продолговатого мозга и уменьшает поток симпатической импульсации к сосудам и сердцу на пресинаптическом уровне.</li>\r\n	<li>При быстром внутривенном введении возможно кратковременное повышение артериального давления, обусловленное стимуляцией постсинаптических альфа1-адренорецепторов сосудов. Увеличивает почечный кровоток; повышает тонус сосудов мозга, уменьшает мозговой кровоток, оказывает выраженное седативное действие. Гипотензивный эффект при введении в вену проявляется через 3–5 минут, достигает максимума через 15–20 минут и сохраняется в течении 4–8 часов. Продолжительность терапевтического эффекта 6–12 часов.</li>\r\n</ul>\r\n<p><strong>Показания</strong></p>\r\n<ul>\r\n	<li>Купирование гипертонического криза.</li>\r\n</ul>");
