INSERT INTO `wp_postmeta` VALUES("2567", "614", "product-text", "<p><strong>Фармакологическое действие</strong></p>\r\n<ul>\r\n	<li>Лидокаин является короткодействующим местным анестетиком амидного типа. В основе его механизма действия лежит уменьшение проницаемости мембраны нейрона для ионов натрия. В результате этого снижается скорость деполяризации и повышается порог возбуждения, приводя к обратимому местному онемению. Лидокаин применяют в целях достижения проводниковой анестезии в различных участках тела и контроля аритмий. Обладает быстрым началом действия (около одной минуты после внутривенного введения и пятнадцати минут после внутримышечного), быстро распространяется в окружающие ткани. Действие продолжается 10–20 минут и около 60–90 минут после внутривенного и внутримышечного введения соответственно.</li>\r\n</ul>\r\n<p><strong>Показания</strong></p>\r\n<ul>\r\n	<li>Местная и регионарная анестезия, проводниковая анестезия при больших и малых вмешательствах.</li>\r\n</ul>");
